import React, { Component } from 'react';

// import { Button } from 'react-bootstrap';
import spotifyLogo from '../spotify.png';

//styles
import './Home.css';

import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import * as playlistsActions from '../actions/playlists';

// api
import api from '../services/api';

//components
import FilterPlaylist from '../components/FilterPlaylist';
import ListPlaylist from '../components/ListPlaylist';

class Home extends Component {
	state = {
		playlists: [],
		expiredToken: false
	};

	async componentDidMount() {
		const response = await api.get('browse/featured-playlists');
		this.props.listPlaylists(response.data.playlists.items);
	}

	syncMethod = () => {
		api
			.get('browse/featured-playlists')
			.then(data => {
				this.props.listPlaylists(data.data.playlists.items);
			})
			.catch(error => {
				if (error.response.status === 401) {
					console.log('Your token has been expired!');
					this.setState({ expiredToken: true });
					// mandar para tela de login e exibir uma mensagem
					setInterval(
						() => window.location.replace('http://localhost:3000/'),
						3000
					);
				}
			});
	};

	componentWillMount() {
		setInterval(() => {
			// window.location.reload();
			this.syncMethod();
		}, 3000);
	}

	render() {
		const { expiredToken } = this.state;
		return (
			<div className="container">
				<div className="title-header">
					<h1 className="title-text">SpotiFood</h1>
					<img className="logo" height={36} src={spotifyLogo} alt="SpotiFood" />
				</div>
				{/* <div className="content-wrapper"> */}
				<div className="row">
					<FilterPlaylist />
					<div className="col-12">
						{!expiredToken ? (
							<ListPlaylist playlists={this.props.playlists} />
						) : (
							<Alert
								dismissible
								show={this.state.filterApplied}
								onClose={this.closeModal}
								variant="success"
								style={{ justifyContent: 'center' }}
							>
								<Alert.Heading>Filters applied!</Alert.Heading>
								<hr />
								<div className="d-flex justify-content-start">
									<Button onClick={this.resetFilters} variant="outline-success">
										Remove Filters
									</Button>
								</div>
							</Alert>
						)}
					</div>
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	playlists: state.playlists
});

const mapDispatchToProps = dispatch =>
	bindActionCreators(playlistsActions, dispatch);

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(Home);
