import React, { Component } from 'react';

import './ListPlaylist.css';

import Playlist from '../components/Playlist';

export default class ListPlaylist extends Component {
	componentDidMount() {
		const debounced = _.debounce(this.handleScroll, 50);
		// window.addEventListener('scroll', this.handleScroll);
		window.addEventListener('scroll', _.debounce(debounced));
	}
	componentWillUnmount() {
		window.removeEventListener('scroll', this.handleScroll);
	}

	handleScroll = e => {
		let scrollTop = e.srcElement.body.scrollTop,
			itemTranslate = Math.min(0, scrollTop / 3 - 60);
		console.log('SCROLL');
	};

	render() {
		const { playlists } = this.props;
		let data = playlists[0];

		if (data !== undefined) {
			return data.map(item => <Playlist key={item.id} playlist={item} />);
		} else {
			return null;
		}
	}
}
