import React, { Component } from 'react';

import { Button } from 'react-bootstrap';

import api from '../services/api';

import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import * as playlistsActions from '../actions/playlists';

import './ListPlaylist.css';

import Playlist from '../components/Playlist';

class ListPlaylist extends Component {
	state = {
		limit: 5,
		offset: 5,
		loadingMore: true
	};

	handleClick = e => {
		const { limit, offset, loadingMore } = this.state;
		this.setState({ loadingMore: true });
		this.setState({
			offset: offset + limit
		});
		// this.setState({
		// 	offset: offset + limit
		// });

		this.state.loadingMore ? alert('Aeeee') : alert('OOhh');

		console.log(this.state);

		const response = api
			.get('browse/featured-playlists', {
				params: {
					limit: 5,
					offset: offset
				}
			})
			.then(data => {
				console.log(data);
				this.setState({ loadingMore: false });
				console.log(this.state);
			})
			.catch(error => {
				console.log(error);
			});

		// this.props.listPlaylists(response.data.playlists.items);
	};

	render() {
		const { playlists } = this.props;
		let data = playlists[0];

		if (data !== undefined) {
			return (
				<>
					{data.map(item => (
						<Playlist key={item.id} playlist={item} />
					))}
					<div className="load-more-button">
						<Button
							onClick={() => this.handleClick()}
							style={{
								backgroundColor: 'rgb(0, 225, 85)',
								border: 0,
								fontWeight: '500'
							}}
						>
							Load More
						</Button>
					</div>
				</>
			);
		} else {
			return null;
		}
	}
}

const mapStateToProps = state => ({
	playlists: state.playlists
});

const mapDispatchToProps = dispatch =>
	bindActionCreators(playlistsActions, dispatch);

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(ListPlaylist);
