import React, { Component } from 'react';

import { Button } from 'react-bootstrap';

import api from '../services/api';

import './ListPlaylist.css';

import Playlist from '../components/Playlist';

class ListPlaylist extends Component {
	async componentDidMount() {
		const response = await api.get('browse/featured-playlists', {
			params: {
				limit: 5
			}
		});
		// this.props.listPlaylists(response.data.playlists.items);
	}

	handleClick = e => {
		console.log('CLICOU');
	};

	render() {
		const { playlists } = this.props;
		let data = playlists[0];

		if (data !== undefined) {
			return (
				<>
					{data.map(item => (
						<Playlist key={item.id} playlist={item} />
					))}
					<div className="load-more-button">
						<Button
							onClick={() => this.handleClick()}
							style={{
								backgroundColor: 'rgb(0, 225, 85)',
								border: 0,
								fontWeight: '500'
							}}
						>
							Load More
						</Button>
					</div>
				</>
			);
		} else {
			return null;
		}
	}
}

export default ListPlaylist;
