import React, { Component } from 'react';

import api from '../services/api';

import './FilterPlaylist.css';

import { InputGroup, FormControl, Button, Collapse } from 'react-bootstrap';

export default class FilterPlaylist extends Component {
	state = {
		search: '',
		playlists: '',
		openFilterOptions: false
	};

	handleNewSearch = async e => {
		if (e.keyCode !== 13) return;

		const search = this.state.search;

		const searchResponse = await api.get('search', {
			params: {
				type: 'playlist',
				q: search,
				limit: 2
			}
		});

		console.log('SEARCH: ', searchResponse);

		this.setState({ search: '' });
	};

	handleSearchChange = e => {
		this.setState({ search: e.target.value });
		console.log(this.state.search);
	};
	render() {
		const { openFilterOptions } = this.state;
		return (
			<>
				<InputGroup className="search-form">
					<InputGroup.Prepend>
						<InputGroup.Text id="basic-addon1">@</InputGroup.Text>
					</InputGroup.Prepend>
					<FormControl
						placeholder="Search a playlist"
						aria-label="Search"
						aria-describedby="basic-addon1"
						value={this.state.search}
						onChange={this.handleSearchChange}
						onKeyDown={this.handleNewSearch}
					/>
				</InputGroup>
				<Button
					onClick={() => this.setState({ open: !open })}
					aria-controls="example-collapse-text"
					aria-expanded={open}
				>
					click
				</Button>
				<Collapse in={this.state.open}>
					<div id="example-collapse-text">
						Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
						terry richardson ad squid. Nihil anim keffiyeh helvetica, craft beer
						labore wes anderson cred nesciunt sapiente ea proident.
					</div>
				</Collapse>
			</>
		);
	}
}
