const INITIAL_STATE = {
	playlists: []
};

export default function playlists(state = INITIAL_STATE.playlists, action) {
	switch (action.type) {
		case 'LIST_PLAYLISTS':
			const state = { ...state, playlists: action.payload };
			return [...state, action.payload];
		case 'SEARCH_PLAYLISTS':
			console.log('reducers');
			console.log(...state);
			console.log(action.playlists);
			console.log([...state, action.playlists]);
			return [...state, action.payload];
		default:
			return state;
	}
}
