export default function playlists(state = [], action) {
	switch (action.type) {
		case 'LIST_PLAYLISTS':
			return [action.playlists];
		case 'SEARCH_PLAYLISTS':
			return [action.playlists, action.searchContent];
		case 'FILTER_PLAYLISTS':
			return [action.playlists];
		default:
			return state;
	}
}
