import React, { Component } from 'react';

import './Login.css';
import spotifyLogo from '../spotify.png';

import { Button } from 'react-bootstrap';

import axios from 'axios';

// import SpotifyLogin from 'react-spotify-login';
// import { clientId, redirectUri } from '../settings';

export default class Login extends Component {
	state = {
		loading: false
	};

	handleSignin = e => {
		e.preventDefault();

		window.open(
			`https://accounts.spotify.com/authorize?client_id=77e2595877a442789caa9dc925f30ef4&response_type=code&redirect_uri=http%3A%2F%2Flocalhost%3A3000&scope=user-read-private%20user-read-email&state=34fFs29kd09`,
			'_self'
		);
	};

	handleLogin = code => {
		axios.post('https://accounts.spotify.com/api/token', {
			firstName: 'Fred',
			lastName: 'Flintstone'
		});
	};

	componentDidMount() {
		const urlParams = new URLSearchParams(window.location.search);
		const code = urlParams.get('code');
		if (code) {
			this.handleLogin(code);
			this.setState({
				loading: true
			});
			// window.location.replace('http://www.w3schools.com');
		}
	}

	// componentWillMount() {
	// 	console.log(window.location);
	// 	const urlParams = new URLSearchParams(window.location.search);
	// 	const code = urlParams.get('code');
	// 	console.log(code);
	// }

	render() {
		return (
			<form className="form-signin">
				<div className="text-center mb-4">
					<img
						className="mb-4"
						src={spotifyLogo}
						alt=""
						width="72"
						height="72"
					/>
					<h1 className="title-text">SpotiFood</h1>
				</div>
				<div className="row justify-content-center">
					<Button
						onClick={this.handleSignin}
						style={{
							backgroundColor: 'rgb(0, 225, 85)',
							border: 0,
							fontWeight: '500',
							textAlign: 'center'
						}}
					>
						Log In with Spotify
					</Button>
				</div>
				{/* <SpotifyLogin
					clientId={clientId}
					redirectUri={redirectUri}
					// onSuccess={onSuccess}
					onFailure={response => console.log(response)}
					onRequest={response => console.log(response)}
				/>
				,{document.getElementById('example')} */}
			</form>
		);
	}
}
